    private void btnTestirajBazuActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnTestirajBazuActionPerformed

        try {
            String url = txtURL.getText().trim();
            String username = txtUsername.getText().trim();
            String password = new String(txtPassword.getText());
            Kontroler.testirajParametrer(url, username, password);
            JOptionPane.showMessageDialog(this, "Uspesno ste se konektovali");
            txtPort.setEnabled(true);
            btnTestPort.setEnabled(true);
        } catch (Exception ex) {
            JOptionPane.showMessageDialog(this, "Pogresni parametri");
        }


    }//GEN-LAST:event_btnTestirajBazuActionPerformed

    private void btnTestPortActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnTestPortActionPerformed
        try {
            if (txtPort.getText().isEmpty()) {
                JOptionPane.showMessageDialog(this, "Unesite broj porta!");
                return;
            }
            int port = Integer.parseInt(txtPort.getText().trim());
            ServerNit.proveriPort(port);
            JOptionPane.showMessageDialog(this, "Port je u redu");
            btnSacuvajPromene.setEnabled(true);

        } catch (IOException ex) {

            JOptionPane.showMessageDialog(this, "Port nije slobodan");
        }
    }//GEN-LAST:event_btnTestPortActionPerformed

