    private void srediPolja() {

        btnTestPort.setEnabled(false);
        txtPort.setEnabled(false);
        btnSacuvajPromene.setEnabled(false);

        txtURL.setText(PropertyReader.getInstance().getURL());
        txtUsername.setText(PropertyReader.getInstance().getUsername());
        txtPassword.setText(PropertyReader.getInstance().getPassword());
    }
